#include <bits/stdc++.h>using namespace std;using lo = long long int;#define pb push_back#define st first#define nd second#define inf (int)1e9#define N (int)1e5 * 5#define mod (int)1e9 + 7#define all(x) (x).begin(), (x).end()#define watch(x) cout << #x << " = " << x << endlint in(){	int a;	cin >> a;	return a;}void solve(){    vector<lo> a;    a.pb(1);    a.pb(2);    lo sum = 2;   	while(a[a.size() - 1] <= 4000000){   		a.pb(a[a.size() - 1] + a[a.size() - 2]);   		sum += (a[a.size() - 1] & 1 ? 0 : a[a.size() - 1]);   	}   	cout << sum;}int main(){    ios_base::sync_with_stdio(0); cin.tie(nullptr); cout.tie(0); cerr.tie(0);    mt19937 rng(5);    // freopen("gir.gir", "r", stdin);    // freopen("cik.cik", "w", stdout);    int t = 1;    // cin >> t;    while(t--){        solve();    }    return 0;}/**/